import {Component, OnDestroy, OnInit, ViewChild} from '@angular/core';
import { Ingredients } from 'src/app/shared/ingredient.model';
import { ShoppingListService } from '../shopping-list.service';
import {NgForm} from "@angular/forms";
import {Subscription} from "rxjs";

@Component({
  selector: 'app-shopping-edit',
  templateUrl: './shopping-edit.component.html',
  styleUrls: ['./shopping-edit.component.css']
})
export class ShoppingEditComponent implements OnInit, OnDestroy {
subscription: Subscription;
editedItemIndex: number;
editedItem: Ingredients;
@ViewChild('f') slForm : NgForm;



  ngOnInit(){
  
  }
 newShopping:Ingredients;
  editMode = false;

 constructor(private sLService : ShoppingListService){
     this.subscription= this.sLService.startedEditing.subscribe(
         (index :number)=>{
             this.editedItemIndex = index;
             this.editMode = true;
             this.editedItem = this.sLService.getIngredient(index);
            this.slForm.setValue(
                {
                    name : this.editedItem.name,
                    amount : this.editedItem.amount

                }
            )

         }

     );

 }

  onSubmit(form : NgForm){
     const value = form.value;
     const newIng = new Ingredients(value.name, value.amount);
     if(this.editMode){
         this.sLService.upgradeIngredient(this.editedItemIndex , newIng)

     }else{
         this.sLService.addIngredients(newIng);
     }
      this.editMode=false;
     form.reset();
  }
    onClear(){
     this.slForm.reset()
        this.editMode=false;

    }
    onDelete(){
     this.sLService.deleteIngredient(this.editedItemIndex);
     // this.onClear();
     console.log(this.editedItemIndex)

    }


    ngOnDestroy(): void {
     this.subscription.unsubscribe();
    }

}
<div class="row">
    <div class="col-xs-12">
        <form (ngSubmit)="onSubmit(f)" #f = "ngForm">
            <div class="row">
                <div class="col-sm-5 form-group">
                    <label for="name">Name</label>
                    <input type="text" 
                    id="name" 
                    class="form-control" name = "name" ngModel required #name = "ngModel"
                    >
                    <span *ngIf="name.invalid && name.touched" class="text-danger">Please enter a name! </span>

                </div>
                <div class="col-sm-2 form-group">
                    <label for="amount">Amount</label>
                    <input type="number"
                           id="amount"
                           class="form-control"
                           name = "amount"
                           ngModel required
                           #amount = "ngModel"
                           [pattern]="'^[1-9]+[0-9]*$'"
                    >
                    <span *ngIf="amount.invalid && amount.touched" class="text-danger">invalid amount</span>
                </div>
                </div>
                <div class="row">
                    <div class="col-xs-12" >
                        <button class="btn btn-success" type="submit" [disabled]="f.invalid" >{{editMode? 'Update' : 'Add' }}</button>
                        <button class="btn btn-danger" type="button" (click)="onDelete()">Delete</button>
                        <button class="btn btn-primary" type="button" (click)="onClear()">Clear</button>
                    </div>
                </div>
           
        </form>
    </div>
</div>
