import {Component, OnInit} from '@angular/core';
import { RecipeService } from '../recipe.service';
import {ActivatedRoute, Params} from "@angular/router";
import {Recipe} from "../recipe.model";
import {ngbCarouselTransitionOut} from "@ng-bootstrap/ng-bootstrap/carousel/carousel-transition";

@Component({
  selector: 'app-recipes-detail',
  templateUrl: './recipes-detail.component.html',
  styleUrls: ['./recipes-detail.component.css']
})
export class RecipesDetailComponent implements OnInit{
    recipe : Recipe;
    id: number;



  constructor(private rService : RecipeService,
              private route : ActivatedRoute){
  }


  addToShoppingList(){

     console.log ( this.recipe.ingredients)
     this.rService.addIngredientsToShoppingList(this.recipe.ingredients);



    //  for(let i =0 ; i < this.recipe.ingredients.length; i++){

    //       this.sLService.addIngredients( this.recipe.ingredients[i]);

     }

    ngOnInit(){
      this.route.params.subscribe(
          (par : Params) => {
              this.id = +par['id'];
              this.recipe = this.rService.getRecipesById(this.id)
              console.log('details')
              console.log(this.recipe)
          }
      )
        console.log('this id is ' + this.id)
    }


  }


<div class="row">
    <div class="col-xs-12">
        <img [src] = "recipe.imagePath" 
        style="height: 170px; width: 250px"
        
        alt="recipe.name" class="image-responsive">
    </div>
</div>
<div class="row">
    <div class="col-xs-12">
        <h1>{{ recipe.name }}</h1>
    </div>
</div>
<div class="row">
    <div class="col-xs-12">
        <div class="btn-group"
        appDropdown
        >
            <button type="button" class="btn btn-primary dropdown-toggle">Manage Recipe<span class="caret"></span></button>
            <ul class="dropdown-menu">
                <li style="cursor: pointer" ><a  (click)="addToShoppingList()">add to my Shopping List</a></li>
                <li style="cursor: pointer" routerLink="edit"><a >Edit Recipe</a></li>
                <li style="cursor: pointer" ><a>Delete Recipe</a></li>
            </ul>
        </div>
    </div>
</div>
<div class="row">
    <div class="col-xs-12">
    {{ recipe.description }}
    </div>
    <div class="col-xs-12">
        <ul class="list-group">
            <hr>
            <p style="color:darkblue; font-style:inherit; font-size: medium;" >ingredients that you need: </p>
          <li class="list-group-item"
          *ngFor="let i of recipe.ingredients">


                {{ i.ingredientName }} - {{ i.amount }}

          </li>  

        </ul>


       
    </div>
</div>
