import { Component } from '@angular/core';
import { NgForm } from '@angular/forms';
import { Router } from '@angular/router';
import { Observable } from 'rxjs';

import { AuthService, AuthResponseData } from './auth.service';

@Component({
    selector: 'app-auth',
    templateUrl: './auth.component.html'
})
export class AuthComponent {
    isLoginMode = true;
    isLoading = false;
    error: string = null;

    constructor(private authService: AuthService, private router: Router) {}

    onSwitchMode() {
        this.isLoginMode = !this.isLoginMode;
    }

    onSubmit(form: NgForm) {
        if (!form.valid) {
            return;
        }
        const email = form.value.email;
        const password = form.value.password;

        let authObs: Observable<AuthResponseData>;

        this.isLoading = true;

        if (this.isLoginMode) {
            authObs = this.authService.login(email, password);
        } else {
            authObs = this.authService.signup(email, password);
        }

        authObs.subscribe(
            resData => {
                console.log(resData);
                this.isLoading = false;
                this.router.navigate(['/recipes']);
            },
            errorMessage => {
                console.log(errorMessage);
                this.error = errorMessage;
                this.isLoading = false;
            }
        );

        form.reset();
    }
}
<div class="container">


    <div class="row" style="margin-top: 40px">
        <div class="col-xs-12 col-md-6 col-md-offset-3" >

            <div *ngIf="isLoading" style="text-align: center ; margin-top: 50px"><app-loading-spinner></app-loading-spinner></div>
            <form [formGroup]="authForm"  (ngSubmit)="onSubmit()" *ngIf="!isLoading">
                <div class="form-group">
                    <label for="email"> Email</label>
                    <input id="email"  type="email" class="form-control" formControlName="email">
                </div>
                <div class="form-group">
                    <label>password</label>
                    <input id="password" type="password" class="form-control" formControlName="password">
                </div>
                <div class="alert alert-danger" *ngIf="error"><p>{{error}}</p></div>
                <div>
                    <button class="btn btn-default" style="margin-right: 20px" type="submit"
                            [disabled]="authForm.invalid"
                    >{{ isLoginMode ? 'Login' : 'register'}}</button>

                    <button class="btn btn-default" (click)="onSwitchMode()">switch to {{ isLoginMode ? 'Sign up' : 'Login'}}</button>
                </div>



            </form>
        </div>
    </div>



</div>
