import {Component, OnInit} from '@angular/core';
import {FormControl, FormGroup, Validators} from "@angular/forms";
import {AuthService} from "./auth.service";

@Component({
  selector: 'app-auth',
  templateUrl: './auth.component.html',
  styleUrls: ['./auth.component.css']
})
export class AuthComponent implements OnInit{
    authForm : FormGroup;
    isloginMode : boolean;
    isLoading = false;
    error :string = null ;

    constructor(private as : AuthService) {
    }




   onSwitchMode(){
     this.isloginMode = !this.isloginMode;
     console.log(this.isloginMode)
   }

    ngOnInit(): void {
       this.authForm = new FormGroup(
           {
               'email': new FormControl('max@test.com',
                   [Validators.required,Validators.email]),

               'password' : new FormControl('' ,
                   [Validators.required , Validators.minLength(1) ])
           }
       )

    }

    onSubmit(){

       if(this.authForm.valid){

           const email = this.authForm.get('email').value
           const password = this.authForm.value['password'];

           this.isLoading = true;
           //等效

           if(this.isloginMode){

           }else {
               this.as.signup(email,password).subscribe(
                   response =>{
                       console.log(response)
                       this.isLoading= false;
                   },errorRes => {
                       console.log(errorRes);
                       switch (errorRes.error.error.message){
                           case 'EMAIL_EXISTS':
                               this.error =  'this email exists already!';
                       }


                       this.isLoading = false;
                   }
               )

               this.authForm.reset();
           }

       }else return;


    }
}
<div class="container">


    <div class="row" style="margin-top: 40px">
        <div class="col-xs-12 col-md-6 col-md-offset-3" >

            <div *ngIf="isLoading" style="text-align: center ; margin-top: 50px"><app-loading-spinner></app-loading-spinner></div>
            <form [formGroup]="authForm"  (ngSubmit)="onSubmit()" *ngIf="!isLoading">
                <div class="form-group">
                    <label for="email"> Email</label>
                    <input id="email"  type="email" class="form-control" formControlName="email">
                </div>
                <div class="form-group">
                    <label>password</label>
                    <input id="password" type="password" class="form-control" formControlName="password">
                </div>
                <div class="alert alert-danger" *ngIf="error"><p>{{error}}</p></div>
                <div>
                    <button class="btn btn-default" style="margin-right: 20px" type="submit"
                            [disabled]="authForm.invalid"
                    >{{ isloginMode ? 'Login' : 'Sign up'}}</button>

                    <button class="btn btn-default" (click)="onSwitchMode()">switch to {{ isloginMode ? 'Sign up' : 'Login'}}</button>
                </div>



            </form>
        </div>
    </div>



</div>
