import { Component , OnInit} from '@angular/core';
import {Recipe} from "./recipe.model";
import { RecipeService } from './recipe.service';

@Component({
  selector: 'app-recipes',
  templateUrl: './recipes.component.html',
  styleUrls: ['./recipes.component.css'],
  providers: [RecipeService]
})
export class RecipesComponent implements OnInit{
  selectedRecipe: Recipe;

  ngOnInit(){
    this.rS.selectedRecipe.subscribe(
      (recipe : Recipe) => {
        this.selectedRecipe = recipe;
      } 



    )
  }

  constructor(private rS : RecipeService){

  }

}

<div class="row">
    <div class="col-md-5">
        <app-recipes-list></app-recipes-list>
    </div>
<div class = "col-md-7">
    <app-recipes-detail *ngIf = "selectedRecipe ; else inforText"
    [recipe]="selectedRecipe"></app-recipes-detail>
     <ng-template #inforText>
      <app-recipe-start routerLink="/start"></app-recipe-start>
     </ng-template>
</div>
</div>
