import { Component, ElementRef, OnInit,  ViewChild } from '@angular/core';
import { Ingredients } from 'src/app/shared/ingredient.model';
import { ShoppingListService } from '../shopping-list.service';

@Component({
  selector: 'app-shopping-edit',
  templateUrl: './shopping-edit.component.html',
  styleUrls: ['./shopping-edit.component.css']
})
export class ShoppingEditComponent implements OnInit {

  @ViewChild('nameInput') nameInputRef :ElementRef;
  @ViewChild('amountInput') amountInputRef :ElementRef;
 

  ngOnInit(){
  
  }
 newShopping:Ingredients;

 constructor(private sLService : ShoppingListService){

 }

  onAddItem(){
    const ingName = this.nameInputRef.nativeElement.value;
    const ingAmount = this.amountInputRef.nativeElement.value 
    const newIng = new Ingredients(ingName, ingAmount);
    

    this.sLService.addIngredients(newIng);

    console.log("hii" + ingName);
    


  }

}
<div class="row">
    <div class="col-xs-12">
        <form (ngSubmit)="onAddItem()">
            <div class="row">
                <div class="col-sm-5 form-group">
                    <label for="name">Name</label>
                    <input type="text" 
                    id="name" 
                    class="form-control"
                    >
                </div>
                <div class="col-sm-2 form-group">
                    <label for="amount">Amount</label>
                    <input type="number" 
                    id="amount" 
                    class="form-control"
                    >
                </div>
                </div>
                <div class="row" style="margin:15px">
                    <div class="col-xs-12" >
                        <button class="btn-success" type="submit">Add</button>
                        <button class="btn-danger" type="button">Delete</button>
                        <button class="btn-primary" type="button">Clear</button>
                    </div>
                </div>
           
        </form>
    </div>
</div>
