    <div class="col-xs-12">
        <form>
            <div class="row">
                <div class="col-sm-5 form-group">
                    <label for="name">Name</label>
                    <input type="text" 
                    id="name" 
                    class="form-control"
                    #nameInput>
                </div>
                <div class="col-sm-2 form-group">
                    <label for="amount">Amount</label>
                    <input type="number" 
                    id="amount" 
                    class="form-control"
                    #amountInput>
                </div>
                </div>
                <div class="row" style="margin:15px">
                    <div class="col-xs-12" >
                        <button class="btn-success" type="button" (click)="onAddItem()">Add</button>
                        <button class="btn-danger" type="button">Delete</button>
                        <button class="btn-primary" type="button">Clear</button>
                    </div>
                </div>
           
        </form>
    </div>
</div>
