import { Injectable } from '@angular/core';
import {HttpClient} from "@angular/common/http";
import {catchError, throwError} from "rxjs";


interface AuthResponseData{
  kind:string;
  idToken: string;
  email:string;
  refreshToken:string;
  expiresIn: string;
  localId:string;
  //response data format
}

@Injectable({
  providedIn: 'root'
})
export class AuthService {

  constructor( private http : HttpClient) { }


  signup(email:string,password:string){
    return  this.http.post<AuthResponseData>('https://identitytoolkit.googleapis.com/v1/accounts:signUp?key=AIzaSyA4hpXtrrJa6GwJQ_c6linszg4kK4FG5T0'
    ,{
            email : email,
            password: password ,
            returnSecureToken : true}).pipe(catchError(errRes=>{
                // catch error 捕获错误 ，并执行一些特殊的操作， 通常是显示错误或者纠正措施
                let errorMessage = 'An unknow errer occured!';
                console.log('1')
                if(!errRes.error||!errRes.error.error){
                    // throw error 抛出错误 告诉程序一个错误发生了
                    return throwError(()=>{
                        new Error(errorMessage);
                        console.log('2')
                    });
                }

                switch (errRes.error.error.message){


                    case 'EMAIL_EXISTS':
                        errorMessage =  'this email exists already!';
                        console.log('3')

                }
        return throwError(()=>{
            new Error(errorMessage);
        });
    }));

  }


}
