import {Injectable} from "@angular/core";
import {HttpClient} from "@angular/common/http";
import {RecipeService} from "../recipes/recipe.service";
import {Recipe} from "../recipes/recipe.model";
import {map, tap} from "rxjs";

@Injectable({providedIn:'root'})
export class DataStorageService{

    constructor(private http : HttpClient , private rs : RecipeService) {
}


storeRecipes(){
      const recipes= this.rs.getRecipes();
      this.http.put
      ('https://ng-course-recipe-381e3-default-rtdb.europe-west1.firebasedatabase.app/recipes.json',
          recipes)
          .subscribe( response =>{
              console.log(response)
          });
}
fetchRecipes(){

        return this.http
            .get<Recipe[]>('https://ng-course-recipe-381e3-default-rtdb.europe-west1.firebasedatabase.app/recipes.json')
            .pipe(
                map(recipes =>{
                return recipes.map(recipe => {
                    return {
                        ...recipe,
                        ingredients : recipe.ingredients  ? recipe.ingredients : []};
                });
            }),
                tap(recipes =>{
                    this.rs.setRecipes(recipes);
                })
            )
            .subscribe(
                recipes => {
                this.rs.setRecipes(recipes);
                console.log(recipes)

            });

}


}
