import {Component, OnDestroy, OnInit} from '@angular/core';
import { Recipe } from '../recipe.model';
import { RecipeService } from '../recipe.service';
import {Subscription} from "rxjs";

@Component({
  selector: 'app-recipes-list',
  templateUrl: './recipes-list.component.html',
  styleUrls: ['./recipes-list.component.css']
})


export class RecipesListComponent implements OnInit, OnDestroy{

  recipes?: Recipe[];
  subscription : Subscription;
  recipeAmount? : number;


  constructor(private rService : RecipeService){}

  ngOnInit() {
    this.recipeAmount = this.recipes.length;

    this.subscription =  this.rService.recipesChange.subscribe(
        (recipes: Recipe[])=> {
          this.recipes = recipes;
        }
    );

    this.recipes = this.rService.getRecipes();
    console.log('currently recipelist are : ')
    console.log(this.recipes)


  }

  ngOnDestroy() {
    this.subscription.unsubscribe();
  }

}
<div class="row">
<div class="col-xs-12">
    <button class="btn btn-success" routerLink="new">New Recipe</button>
</div>
</div>
<hr>

<div class="row">
    <div class="col-xs-12">
        <app-recipes-item
          *ngFor="let r of recipes,  let i = index"
          [recipe]="r"
          [index] = "i"
           ></app-recipes-item>
    </div>
</div>
