})
export class AppComponent implements OnInit{
  title = "none"
 loadedFeature = "recipe";
  onNavigate(feature: string){
    this.loadedFeature = feature;

  }

  ngOnInit(){
  }
}


<app-header (featureSelected)="onNavigate($event)"></app-header>

